mapper.select:
select resell_no, resell_name, resell_seller, item_price, date_format(resell_writedate, '%Y-%m-%d %h:%i:%s')
resell_writedate from resell order by resell_writedate desc
limit #{onePageRecord} offset ${offsetPoint};

select item_no, item_name, item_price, item_image, item_postdate
from item order by item_postdate desc
limit #{onePageRecord} offset ${offsetPoint};
